package com.project;

import java.math.BigInteger;

public class SetKeys {
    // client keys: encrypt using public
    private final static BigInteger publicKeyModules = new BigInteger("");
    private final static BigInteger publicKeyExpo = new BigInteger("65537");

    // client keys: decrypt using private
    private final static BigInteger privateKeyModules = new BigInteger("");
    private final static BigInteger privateKeyExpo = new BigInteger("");

    BigInteger getPublicKeyModules() {
        return publicKeyModules;
    }

    BigInteger getPublicKeyExpo() {
        return publicKeyExpo;
    }

    BigInteger getPrivateKeyModules() {
        return privateKeyModules;
    }

    BigInteger getPrivateKeyExpo() {
        return privateKeyExpo;
    }
}
